import React, { useState, useEffect } from 'react';
import './Shoppage.css';
import Products from '../components/Products.js';
import axios from 'axios';
import { useSelector, useDispatch } from 'react-redux';
import { listProducts } from '../actions/shopActions';
import {ProductCard} from 'react-ui-cards';

export default function Shoppage(props) 
{
    // const [loading, setLoading] = useState(true);
    // let [shops, setShops] = useState([null]);
    const productList = useSelector(state => state.productList);
    const { products, loading, error } = productList;
    const dispatch = useDispatch();

    useEffect( () => 
    {
        dispatch(listProducts(props.match.params.id));
    //     const fetchdata = async () => 
    //     {
    //         let { data } = await axios.get("/api/shops");
    //         data = await data.find( x => x._id === props.match.params.id);
    //         setShops(data);
    //         setLoading(false);
    //     }
    //    fetchdata();
        return () => {
            //
        };
    }, [props.match.params.id])
    console.log(products)
    return (
        loading ? <div>loading...</div> : error ? {error} :
        <div>
            <h1>{products.name}</h1>
            <h3>{products.description}</h3>

            {/* <Products products={products}></Products> */}

            {            
                    products.productItems.map((productt) => {
                    return(
                        <div >
                            <ProductCard
                            photos={[
                                'https://i.imgur.com/jRVDeI8.jpg',
                                'https://i.imgur.com/raPe27t.jpg',
                                'https://i.imgur.com/IpEsYSH.jpg'
                            ]}
                            price={productt.pprice}
                            productName={productt.pname}
                            description='kskcsmsmcklsclksmdlkcm ldsmclkclkdsclks clskmdclksmclmcl lmslcmlksmclksc lmslkcmlkms'
                            buttonText='Add to cart'
                            rating={3}
                            url=""
                            />
                        </div>
                    )})
                }

        </div>
    )
}
