import Axios from "axios"
import { ADD_TO_CART } from "../constants/cartConstants";

const addToCart = (shopID,productID) => async (dispatch) => {
    try{
        const {data} = await Axios.get("/api/shops/"+shopID);

        dispatch({type: ADD_TO_CART, payload:{
            product: data.productItems[productID]._pid,
            pname: data.productItems[productID].pname,
            pprice: data.productItems[productID].pprice,
            pdesc: data.productItems[productID].pdesc
        }})

    } catch(error) {

    }

}

export {addToCart}
