    //    fetchdata();
        return () => {
            //
        };
    }, [])
//    console.log(products)
    return (
        loading ? <div>loading...</div> : error ? {error} :
        <div>
            <h1>{products.name}</h1>
            <h3>{products.description}</h3>
            <Products products={products.productss}></Products>

        </div>
    )
}
