import React, { useState, useEffect } from 'react';
import './Shoppage.css';
import Products from '../components/Products.js';
import axios from 'axios';

export default function Shoppage(props) 
{
    // console.log(props.match.params.id);
    const [loading, setLoading] = useState(true);
    let [shops, setShops] = useState([null]);
    useEffect( () => 
    {
        const fetchdata = async () => 
        {
            let { data } = await axios.get("/api/shops");
            data = await data.find( x => x._id === props.match.params.id);
            setShops(data);
            setLoading(false);
        }
       fetchdata();
        return () => {
            //
        }
    }, [props.match.params.id])

    return (

        <div>
            <h1>{shops.name}</h1>
            <h3>{shops.description}</h3>
            {loading ? <div>loading..</div> : <Products products={shops.products}></Products>}

        </div>
    )
}
