import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { signin } from '../actions/sellerActions';
import { saveShop } from '../actions/shopActions';

export default function Sellerinventorypage(props) {

    const [modalVisible, setModalVisible] = useState(false);
    //const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [desc, setDesc] = useState('');
    const [category, setCategory] = useState('');

    const shopSave = useSelector(state => state.shopSave);
    const { loading: loadingSave, success: successSave, error: errorSave } = shopSave;
    const dispatch = useDispatch();

    useEffect(() => {
        if (successSave) {
          //setModalVisible(false);
        }
        dispatch(listProducts());
        return () => {
          //
        };
      }, [successSave]);

    const openModal = (shop) => {
        setModalVisible(true);
        //setId(product._id);
        setName(shop.name);
        setDesc(shop.desc);
        setCategory(shop.category);
    }

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(saveShop({
            //_id: id,
            name, desc, category
        }));
    }

    return (
        <div className="content content-margined">

            <div className="product-header">
            <h3>Products</h3>
            <button className="button primary" onClick={() => openModal({})}>Create Product</button>
            </div>
            {
            modalVisible &&
            <div className="form">
                <form onSubmit={submitHandler} >
                <ul className="form-container">
                    <li>
                    <h2>Create Product</h2>
                    </li>
                    <li>
                    {loadingSave && <div>Loading...</div>}
                    {errorSave && <div>{errorSave}</div>}
                    </li>

                    <li>
                    <label htmlFor="name">
                        Name
                </label>
                    <input type="text" name="name" value={name} id="name" onChange={(e) => setName(e.target.value)}>
                    </input>
                    </li>
                    <li>
                    <label htmlFor="price">
                        Price
                </label>
                    <input type="text" name="price" value={price} id="price" onChange={(e) => setPrice(e.target.value)}>
                    </input>
                    </li>
                    <li>
                    <label htmlFor="image">
                        Image
                </label>
                    <input type="text" name="image" value={image} id="image" onChange={(e) => setImage(e.target.value)}>
                    </input>
                    </li>
                    <li>
                    <label htmlFor="brand">
                        Brand
                </label>
                    <input type="text" name="brand" value={brand} id="brand" onChange={(e) => setBrand(e.target.value)}>
                    </input>
                    </li>
                    <li>
                    <label htmlFor="countInStock">
                        CountInStock
                </label>
                    <input type="text" name="countInStock" value={countInStock} id="countInStock" onChange={(e) => setCountInStock(e.target.value)}>
                    </input>
                    </li>
                    <li>
                    <label htmlFor="name">
                        Category
                </label>
                    <input type="text" name="category" value={category} id="category" onChange={(e) => setCategory(e.target.value)}>
                    </input>
                    </li>
                    <li>
                    <label htmlFor="description">
                        Description
                </label>
                    <textarea name="description" value={description} id="description" onChange={(e) => setDescription(e.target.value)}></textarea>
                    </li>
                    <li>
                    <button type="submit" className="button primary">{id ? "Update" : "Create"}</button>
                    </li>
                    <li>
                    <button type="button" onClick={() => setModalVisible(false)} className="button secondary">Back</button>
                    </li>
                </ul>
                </form>
            </div>
            }
    </div>
    )
}
